/* ========================================
   ENHANCED COMPONENTS STYLES
   ======================================== */

/* Color Variables */
:root {
  --status-active: #10b981;
  --status-suspended: #f59e0b;
  --status-banned: #ef4444;
  --status-pending: #6366f1;
  --reason-spam: #f59e0b;
  --reason-harassment: #ef4444;
  --reason-hate: #dc2626;
  --reason-other: #6b7280;
}

/* Section Headers */
.section-header {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  margin-bottom: 30px;
}

.section-subtitle {
  color: var\(--text-muted\);
  margin: 8px 0 0 0;
  font-size: 14px;
}

.section-actions {
  display: flex;
  gap: 10px;
}

/* Modal Styles */
.modal-backdrop {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba\(0, 0, 0, 0.7\);
  backdrop-filter: blur\(8px\);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  opacity: 0;
  transition: opacity 0.3s ease;
}

.modal-backdrop.visible {
  opacity: 1;
}

.modal-container {
  background: var\(--bg-glass\);
  backdrop-filter: blur\(20px\);
  border: 1px solid var\(--border-glass\);
  border-radius: var\(--radius-lg\);
  box-shadow: 0 20px 60px rgba\(0, 0, 0, 0.5\);
  max-height: 90vh;
  overflow: hidden;
  display: flex;
  flex-direction: column;
  transform: translateY\(50px\);
  opacity: 0;
  transition: all 0.3s cubic-bezier\(0.4, 0, 0.2, 1\);
}

.modal-container.visible {
  transform: translateY\(0\);
  opacity: 1;
}

.modal-small { width: 400px; max-width: 90vw; }
.modal-medium { width: 600px; max-width: 90vw; }
.modal-large { width: 800px; max-width: 90vw; }

.modal-header {
  padding: 24px 30px;
  border-bottom: 1px solid var\(--border-glass\);
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.modal-header h3 {
  margin: 0;
  font-size: 20px;
  font-weight: 600;
}

.modal-close {
  background: none;
  border: none;
  color: var\(--text-muted\);
  cursor: pointer;
  padding: 4px;
  border-radius: var\(--radius-sm\);
  transition: all var\(--transition-fast\);
  display: flex;
  align-items: center;
  justify-center;
}

.modal-close:hover {
  color: var\(--text-primary\);
  background: var\(--bg-glass\);
}

.modal-body {
  padding: 30px;
  overflow-y: auto;
  flex: 1;
}

.modal-footer {
  padding: 20px 30px;
  border-top: 1px solid var\(--border-glass\);
  display: flex;
  gap: 10px;
  justify-content: flex-end;
}

/* Form Enhancements */
.form-group {
  margin-bottom: 20px;
}

.form-group label {
  display: block;
  margin-bottom: 8px;
  font-weight: 500;
  color: var\(--text-primary\);
}

.form-control {
  width: 100%;
  padding: 10px 14px;
  background: var\(--bg-glass\);
  border: 1px solid var\(--border-glass\);
  border-radius: var\(--radius-md\);
  color: var\(--text-primary\);
  font-size: 14px;
  transition: all var\(--transition-fast\);
  font-family: inherit;
}

.form-control:focus {
  outline: none;
  border-color: var\(--primary\);
  box-shadow: 0 0 0 3px rgba\(99, 102, 241, 0.1\);
}

.form-control::placeholder {
  color: var\(--text-muted\);
}

.form-group.has-error .form-control {
  border-color: var\(--danger\);
}

.form-error {
  color: var\(--danger\);
  font-size: 13px;
  margin-top: 6px;
  display: none;
}

.form-group.has-error .form-error {
  display: block;
}

.form-help {
  color: var\(--text-muted\);
  font-size: 13px;
  margin-top: 6px;
}

.char-counter {
  text-align: right;
  color: var\(--text-muted\);
  font-size: 12px;
  margin-top: 4px;
}

.radio-group {
  display: flex;
  flex-direction: column;
  gap: 10px;
}

.radio-label {
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 12px;
  background: var\(--bg-glass\);
  border: 1px solid var\(--border-glass\);
  border-radius: var\(--radius-md\);
  cursor: pointer;
  transition: all var\(--transition-fast\);
}

.radio-label:hover {
  border-color: var\(--primary\);
  background: rgba\(99, 102, 241, 0.05\);
}

.radio-label input[type="radio"] {
  cursor: pointer;
}

.required {
  color: var\(--danger\);
}

/* Table Enhancements */
.table-wrapper {
  background: var\(--bg-glass\);
  backdrop-filter: blur\(20px\);
  border: 1px solid var\(--border-glass\);
  border-radius: var\(--radius-lg\);
  overflow: hidden;
}

.table-header {
  padding: 20px 24px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  border-bottom: 1px solid var\(--border-glass\);
}

.table-header h3 {
  margin: 0;
  font-size: 16px;
  font-weight: 600;
}

.table-header-left,
.table-header-right {
  display: flex;
  gap: 12px;
  align-items: center;
}

.search-bar {
  position: relative;
  min-width: 280px;
}

.search-input {
  width: 100%;
  padding: 8px 12px 8px 36px;
  background: var\(--bg-glass\);
  border: 1px solid var\(--border-glass\);
  border-radius: var\(--radius-md\);
  color: var\(--text-primary\);
  font-size: 14px;
  transition: all var\(--transition-fast\);
}

.search-input:focus {
  outline: none;
  border-color: var\(--primary\);
}

.search-icon {
  position: absolute;
  left: 12px;
  top: 50%;
  transform: translateY\(-50%\);
  color: var\(--text-muted\);
  pointer-events: none;
}

.filter-group {
  display: flex;
  align-items: center;
  gap: 8px;
}

.filter-label {
  font-size: 13px;
  color: var\(--text-muted\);
  white-space: nowrap;
}

.filter-select {
  padding: 8px 12px;
  background: var\(--bg-glass\);
  border: 1px solid var\(--border-glass\);
  border-radius: var\(--radius-md\);
  color: var\(--text-primary\);
  font-size: 14px;
  cursor: pointer;
  transition: all var\(--transition-fast\);
}

.filter-select:focus {
  outline: none;
  border-color: var\(--primary\);
}

.filter-tabs {
  display: flex;
  gap: 8px;
  margin-bottom: 24px;
  padding: 0 24px;
}

.filter-tab {
  padding: 10px 20px;
  background: var\(--bg-glass\);
  border: 1px solid var\(--border-glass\);
  border-radius: var\(--radius-md\);
  color: var\(--text-muted\);
  cursor: pointer;
  font-size: 14px;
  font-weight: 500;
  transition: all var\(--transition-fast\);
}

.filter-tab:hover {
  color: var\(--text-primary\);
  border-color: var\(--primary\);
  background: rgba\(99, 102, 241, 0.05\);
}

.filter-tab.active {
  color: var\(--primary\);
  border-color: var\(--primary\);
  background: rgba\(99, 102, 241, 0.1\);
}

.table-scroll {
  overflow-x: auto;
}

.data-table {
  width: 100%;
  border-collapse: collapse;
}

.data-table thead th {
  text-align: left;
  padding: 14px 20px;
  font-size: 13px;
  font-weight: 600;
  color: var\(--text-muted\);
  text-transform: uppercase;
  letter-spacing: 0.5px;
  border-bottom: 1px solid var\(--border-glass\);
  background: rgba\(255, 255, 255, 0.02\);
}

.data-table tbody td {
  padding: 16px 20px;
  border-bottom: 1px solid var\(--border-glass\);
  font-size: 14px;
}

.table-row {
  transition: all var\(--transition-fast\);
  opacity: 0;
  transform: translateY\(10px\);
}

.table-row.visible {
  opacity: 1;
  transform: translateY\(0\);
}

.table-row:hover {
  background: rgba\(255, 255, 255, 0.02\);
}

.table-avatar-wrapper {
  position: relative;
  display: inline-flex;
  align-items: center;
  justify-content: center;
}

.table-avatar {
  width: 36px;
  height: 36px;
  border-radius: 50%;
  object-fit: cover;
  border: 2px solid var\(--border-glass\);
}

.user-avatar-sm {
  width: 48px;
  height: 48px;
  border-radius: 50%;
  object-fit: cover;
  border: 2px solid var\(--border-glass\);
}

/* Status Dot */
.status-dot {
  width: 10px;
  height: 10px;
  border-radius: 50%;
  background: var\(--text-muted\);
  position: absolute;
  bottom: 0;
  right: 0;
  border: 2px solid var\(--bg-secondary\);
}

.status-dot.online {
  background: var\(--status-active\);
  box-shadow: 0 0 0 2px rgba\(16, 185, 129, 0.2\);
  animation: pulse 2s infinite;
}

@keyframes pulse {
  0%, 100% {
    opacity: 1;
    transform: scale\(1\);
  }
  50% {
    opacity: 0.8;
    transform: scale\(1.1\);
  }
}

/* Pagination */
.pagination {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 16px 24px;
  border-top: 1px solid var\(--border-glass\);
}

.pagination-info {
  color: var\(--text-muted\);
  font-size: 14px;
}

.pagination-controls {
  display: flex;
  gap: 8px;
  align-items: center;
}

.pagination-btn {
  padding: 8px 16px;
  background: var\(--bg-glass\);
  border: 1px solid var\(--border-glass\);
  border-radius: var\(--radius-md\);
  color: var\(--text-primary\);
  font-size: 14px;
  cursor: pointer;
  transition: all var\(--transition-fast\);
  display: flex;
  align-items: center;
  gap: 6px;
}

.pagination-btn:hover:not\(:disabled\) {
  border-color: var\(--primary\);
  background: rgba\(99, 102, 241, 0.05\);
}

.pagination-btn:disabled {
  opacity: 0.4;
  cursor: not-allowed;
}

.pagination-pages {
  display: flex;
  gap: 4px;
}

.pagination-page {
  padding: 8px 12px;
  background: var\(--bg-glass\);
  border: 1px solid var\(--border-glass\);
  border-radius: var\(--radius-md\);
  color: var\(--text-primary\);
  font-size: 14px;
  cursor: pointer;
  transition: all var\(--transition-fast\);
  min-width: 36px;
  text-align: center;
}

.pagination-page:hover {
  border-color: var\(--primary\);
  background: rgba\(99, 102, 241, 0.05\);
}

.pagination-page.active {
  background: var\(--primary\);
  border-color: var\(--primary\);
  color: white;
}

/* Button Enhancements */
.btn-icon {
  width: 32px;
  height: 32px;
  padding: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  background: var\(--bg-glass\);
  border: 1px solid var\(--border-glass\);
  border-radius: var\(--radius-md\);
  color: var\(--text-primary\);
  cursor: pointer;
  transition: all var\(--transition-fast\);
}

.btn-icon:hover {
  border-color: var\(--primary\);
  color: var\(--primary\);
  background: rgba\(99, 102, 241, 0.05\);
}

.btn-icon.btn-danger:hover {
  border-color: var\(--danger\);
  color: var\(--danger\);
  background: rgba\(239, 68, 68, 0.05\);
}

.btn-icon.btn-success:hover {
  border-color: var\(--success\);
  color: var\(--success\);
  background: rgba\(16, 185, 129, 0.05\);
}

.btn-group {
  display: flex;
  gap: 6px;
}

.btn-ghost {
  background: transparent;
  border: 1px solid transparent;
  color: var\(--text-muted\);
}

.btn-ghost:hover {
  background: var\(--bg-glass\);
  border-color: var\(--border-glass\);
  color: var\(--text-primary\);
}

/* Toast Notifications */
.toast-container {
  position: fixed;
  top: 20px;
  right: 20px;
  z-index: 2000;
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.toast {
  min-width: 320px;
  padding: 16px 20px;
  background: var\(--bg-glass\);
  backdrop-filter: blur\(20px\);
  border: 1px solid var\(--border-glass\);
  border-radius: var\(--radius-lg\);
  box-shadow: var\(--shadow-glow\);
  display: flex;
  align-items: center;
  gap: 12px;
  transform: translateX\(400px\);
  opacity: 0;
  transition: all 0.3s cubic-bezier\(0.4, 0, 0.2, 1\);
}

.toast.visible {
  transform: translateX\(0\);
  opacity: 1;
}

.toast-icon {
  flex-shrink: 0;
}

.toast-message {
  flex: 1;
  font-size: 14px;
  color: var\(--text-primary\);
}

.toast-close {
  flex-shrink: 0;
  background: none;
  border: none;
  color: var\(--text-muted\);
  cursor: pointer;
  padding: 4px;
  border-radius: var\(--radius-sm\);
  transition: all var\(--transition-fast\);
  display: flex;
  align-items: center;
  justify-content: center;
}

.toast-close:hover {
  color: var\(--text-primary\);
  background: rgba\(255, 255, 255, 0.05\);
}

.toast-success {
  border-left: 3px solid var\(--success\);
}

.toast-success .toast-icon {
  color: var\(--success\);
}

.toast-error {
  border-left: 3px solid var\(--danger\);
}

.toast-error .toast-icon {
  color: var\(--danger\);
}

.toast-warning {
  border-left: 3px solid var\(--warning\);
}

.toast-warning .toast-icon {
  color: var\(--warning\);
}

.toast-info {
  border-left: 3px solid var\(--info\);
}

.toast-info .toast-icon {
  color: var\(--info\);
}

/* Charts */
.charts-grid {
  display: grid;
  grid-template-columns: repeat\(auto-fit, minmax\(400px, 1fr\)\);
  gap: 24px;
  margin-bottom: 40px;
}

.chart-card {
  background: var\(--bg-glass\);
  backdrop-filter: blur\(20px\);
  border: 1px solid var\(--border-glass\);
  border-radius: var\(--radius-lg\);
  padding: 24px;
  height: 350px;
  display: flex;
  flex-direction: column;
}

.chart-header {
  margin-bottom: 20px;
}

.chart-header h3 {
  margin: 0 0 4px 0;
  font-size: 16px;
  font-weight: 600;
}

.chart-subtitle {
  font-size: 13px;
  color: var\(--text-muted\);
}

.chart-container {
  flex: 1;
  position: relative;
}

/* Tabs */
.tabs {
  display: flex;
  gap: 8px;
  border-bottom: 1px solid var\(--border-glass\);
  margin-bottom: 24px;
}

.tab {
  padding: 12px 20px;
  background: none;
  border: none;
  border-bottom: 2px solid transparent;
  color: var\(--text-muted\);
  cursor: pointer;
  font-size: 14px;
  font-weight: 500;
  transition: all var\(--transition-fast\);
}

.tab:hover {
  color: var\(--text-primary\);
}

.tab.active {
  color: var\(--primary\);
  border-bottom-color: var\(--primary\);
}

.tab-content {
  position: relative;
}

.tab-panel {
  display: none;
}

.tab-panel.active {
  display: block;
  animation: fadeIn 0.3s ease;
}

@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY\(10px\);
  }
  to {
    opacity: 1;
    transform: translateY\(0\);
  }
}

/* Detail Views */
.user-detail,
.report-detail,
.broadcast-detail {
  max-width: 100%;
}

.user-detail-header {
  display: flex;
  align-items: center;
  gap: 20px;
  padding: 20px;
  background: var\(--bg-glass\);
  border-radius: var\(--radius-lg\);
  margin-bottom: 24px;
}

.user-detail-avatar {
  width: 80px;
  height: 80px;
  border-radius: 50%;
  object-fit: cover;
  border: 3px solid var\(--border-glass\);
}

.user-detail-info h3 {
  margin: 0 0 4px 0;
  font-size: 20px;
  font-weight: 600;
}

.user-phone {
  color: var\(--text-muted\);
  font-size: 14px;
  margin: 0;
}

.detail-section {
  margin-bottom: 30px;
}

.detail-section h4 {
  margin: 0 0 16px 0;
  font-size: 14px;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  color: var\(--text-muted\);
}

.detail-grid {
  display: grid;
  grid-template-columns: repeat\(auto-fill, minmax\(200px, 1fr\)\);
  gap: 20px;
}

.detail-item label {
  display: block;
  font-size: 12px;
  color: var\(--text-muted\);
  text-transform: uppercase;
  letter-spacing: 0.5px;
  margin-bottom: 6px;
}

.detail-item p {
  margin: 0;
  font-size: 14px;
  color: var\(--text-primary\);
}

.detail-item code {
  font-family: 'Courier New', monospace;
  font-size: 12px;
  padding: 2px 6px;
  background: rgba\(255, 255, 255, 0.05\);
  border-radius: 4px;
}

.user-info-compact {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 12px;
  background: var\(--bg-glass\);
  border-radius: var\(--radius-md\);
}

.stats-grid-modal {
  display: grid;
  grid-template-columns: repeat\(auto-fit, minmax\(120px, 1fr\)\);
  gap: 16px;
}

.stat-item-modal {
  text-align: center;
  padding: 20px;
  background: var\(--bg-glass\);
  border-radius: var\(--radius-lg\);
  border: 1px solid var\(--border-glass\);
}

.stat-value-modal {
  font-size: 28px;
  font-weight: 700;
  background: var\(--gradient-primary\);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  margin-bottom: 8px;
}

.stat-label-modal {
  font-size: 12px;
  color: var\(--text-muted\);
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

/* Empty & Error States */
.empty-state,
.error-state {
  text-align: center;
  padding: 60px 20px;
  color: var\(--text-muted\);
}

.empty-state-content,
.error-state-content {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 16px;
}

.empty-state svg,
.error-state svg {
  opacity: 0.5;
}

.empty-state p,
.error-state p {
  margin: 0;
  font-size: 15px;
}

/* Broadcast Preview */
.broadcast-preview {
  margin-top: 24px;
  padding: 20px;
  background: rgba\(255, 255, 255, 0.02\);
  border-radius: var\(--radius-lg\);
  border: 1px solid var\(--border-glass\);
}

.broadcast-preview h4 {
  margin: 0 0 16px 0;
  font-size: 14px;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  color: var\(--text-muted\);
}

.notification-preview-card {
  display: flex;
  gap: 16px;
  padding: 16px;
  background: var\(--bg-glass\);
  border-radius: var\(--radius-md\);
  border: 1px solid var\(--border-glass\);
}

.notification-preview-icon {
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: var\(--gradient-primary\);
  border-radius: 50%;
  flex-shrink: 0;
}

.notification-preview-icon svg {
  stroke: white;
}

.notification-preview-content strong {
  display: block;
  margin-bottom: 4px;
  font-size: 14px;
  color: var\(--text-primary\);
}

.notification-preview-content p {
  margin: 0;
  font-size: 13px;
  color: var\(--text-muted\);
  line-height: 1.5;
}

/* Coming Soon Placeholder */
.coming-soon-container {
  display: flex;
  align-items: center;
  justify-content: center;
  min-height: 60vh;
  padding: 40px 20px;
}

.coming-soon-content {
  text-align: center;
  max-width: 600px;
}

.coming-soon-icon {
  margin-bottom: 30px;
}

.pulse-icon {
  animation: pulse-glow 2s ease-in-out infinite;
}

@keyframes pulse-glow {
  0%, 100% {
    opacity: 1;
    filter: drop-shadow\(0 0 20px rgba\(99, 102, 241, 0.5\)\);
  }
  50% {
    opacity: 0.8;
    filter: drop-shadow\(0 0 40px rgba\(168, 85, 247, 0.7\)\);
  }
}

.coming-soon-title {
  font-size: 32px;
  font-weight: 700;
  margin: 0 0 12px 0;
  background: var\(--gradient-primary\);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

.coming-soon-subtitle {
  font-size: 16px;
  color: var\(--text-muted\);
  margin: 0 0 40px 0;
}

.coming-soon-features {
  display: flex;
  flex-direction: column;
  gap: 16px;
  margin-bottom: 40px;
  text-align: left;
}

.feature-item {
  display: flex;
  align-items: center;
  gap: 12px;
  padding: 16px;
  background: var\(--bg-glass\);
  border: 1px solid var\(--border-glass\);
  border-radius: var\(--radius-lg\);
  transition: all var\(--transition-smooth\);
}

.feature-item:hover {
  border-color: var\(--primary\);
  background: rgba\(99, 102, 241, 0.05\);
  transform: translateX\(8px\);
}

.feature-icon {
  color: var\(--success\);
  flex-shrink: 0;
}

.coming-soon-badge {
  display: inline-flex;
  align-items: center;
  gap: 8px;
  padding: 10px 20px;
  background: var\(--bg-glass\);
  border: 1px solid var\(--border-glass\);
  border-radius: var\(--radius-full\);
  font-size: 14px;
  color: var\(--text-muted\);
  margin-bottom: 30px;
}

/* Last Update */
.last-update {
  text-align: center;
  padding: 16px;
  color: var\(--text-muted\);
  font-size: 13px;
  margin-top: 20px;
}

/* Stat Card Icon */
.stat-icon {
  width: 48px;
  height: 48px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: var\(--gradient-primary\);
  border-radius: var\(--radius-lg\);
  margin-bottom: 12px;
}

.stat-icon svg {
  stroke: white;
  width: 24px;
  height: 24px;
}

/* Skeleton Loader */
.skeleton {
  background: linear-gradient\(
    90deg,
    rgba\(255, 255, 255, 0.05\) 25%,
    rgba\(255, 255, 255, 0.1\) 50%,
    rgba\(255, 255, 255, 0.05\) 75%
  \);
  background-size: 200% 100%;
  animation: shimmer 1.5s infinite;
  border-radius: var\(--radius-md\);
}

@keyframes shimmer {
  0% {
    background-position: -200% 0;
  }
  100% {
    background-position: 200% 0;
  }
}

.skeleton-text {
  height: 16px;
  margin-bottom: 8px;
}

.skeleton-avatar {
  width: 40px;
  height: 40px;
  border-radius: 50%;
}

/* Responsive Enhancements */
@media \(max-width: 768px\) {
  .charts-grid {
    grid-template-columns: 1fr;
  }

  .chart-card {
    height: 280px;
  }

  .modal-container {
    width: 95vw !important;
    margin: 0 10px;
  }

  .search-bar {
    min-width: auto;
    flex: 1;
  }

  .table-header-left {
    flex-wrap: wrap;
  }

  .filter-tabs {
    overflow-x: auto;
    padding: 0 20px;
  }

  .pagination {
    flex-direction: column;
    gap: 12px;
  }

  .detail-grid {
    grid-template-columns: 1fr;
  }

  .user-detail-header {
    flex-direction: column;
    text-align: center;
  }

  .toast-container {
    left: 20px;
    right: 20px;
  }

  .toast {
    min-width: auto;
  }
}

